    def play(self) :
        self.timer=QTimer(self)
        self.timer.start(600)
        self.timer.timeout.connect(lambda:self.popp(opp[self.i]))
            
    def popp(self,p) :
        self.startB(p)
        self.i +=1
        if self.i==len(opp) :
            self.i = 0
            self.timer.stop()      
            
    def stopp(self) :
        self.timer.stop()
        
    def restart(self) :
        self.timer.stop()
        self.i = 0
        self.doA()
        self.play()
    
    def getpre(self) :
        if self.i >0 :
            self.i = self.i -1
            self.startB(opp[self.i])
        
    def getnxt(self) :
        
        if self.i +1<len(opp) :
            self.i = self.i +1
            self.startB(opp[self.i])
            
